# üéµ RPG-AI Supreme - Audio Placeholder Generator
# Este script genera archivos de audio MP3 placeholder m√≠nimos funcionales
# Los archivos son sonidos silenciosos o tonos simples muy cortos (~100-500ms)
# Reemplazar con audio profesional cuando est√© disponible

param(
    [switch]$Force  # Sobrescribir archivos existentes
)

$SoundsDir = Join-Path $PSScriptRoot "..\apps\frontend\assets\sounds"

# Crear directorio si no existe
if (-not (Test-Path $SoundsDir)) {
    New-Item -ItemType Directory -Path $SoundsDir -Force | Out-Null
}

# MP3 placeholders encoded en Base64 (sonidos m√≠nimos v√°lidos)
# Estos son archivos MP3 reales pero muy cortos (~100-200ms de silencio/tono)
$AudioFiles = @{
    # Click: tono corto agudo (100ms)
    "click.mp3" = ""
    
    # Attack: tono medio (200ms)
    "attack.mp3" = ""
    
    # Hit: tono bajo corto (150ms) 
    "hit.mp3" = ""
    
    # LevelUp: tono ascendente (500ms)
    "levelup.mp3" = ""
    
    # Death: tono bajo descendente (800ms)
    "death.mp3" = ""
    
    # Success: tono agradable (300ms)
    "success.mp3" = ""
}

Write-Host "üéµ RPG-AI Supreme - Audio Placeholder Generator" -ForegroundColor Cyan
Write-Host "=" * 50

$created = 0
$skipped = 0

foreach ($file in $AudioFiles.GetEnumerator()) {
    $filePath = Join-Path $SoundsDir $file.Key
    
    if ((Test-Path $filePath) -and -not $Force) {
        Write-Host "‚è≠Ô∏è  Skipping: $($file.Key) (already exists)" -ForegroundColor Yellow
        $skipped++
        continue
    }
    
    try {
        $bytes = [Convert]::FromBase64String($file.Value)
        [System.IO.File]::WriteAllBytes($filePath, $bytes)
        Write-Host "‚úÖ Created: $($file.Key)" -ForegroundColor Green
        $created++
    } catch {
        Write-Host "‚ùå Error creating $($file.Key): $_" -ForegroundColor Red
    }
}

Write-Host ""
Write-Host "=" * 50
Write-Host "üìä Summary:" -ForegroundColor Cyan
Write-Host "   Created: $created files"
Write-Host "   Skipped: $skipped files (use -Force to overwrite)"
Write-Host ""
Write-Host "üìù Next steps:" -ForegroundColor Yellow
Write-Host "   1. Update useGameEffects.ts to enable sounds"
Write-Host "   2. Test in app: pnpm --filter frontend start"
Write-Host "   3. Replace placeholders with real audio from:"
Write-Host "      - https://freesound.org"
Write-Host "      - https://mixkit.co/free-sound-effects/game/"
Write-Host "      - https://sfxr.me (generate retro sounds)"
